BEGIN
  SELECT COUNT(1) INTO V_COUNT FROM EMS_DASHBOARD WHERE TENANT_ID='&TENANT_ID' AND DASHBOARD_ID=MW_RESOURCE_DSB_ID;
  IF (V_COUNT>0) THEN
    DBMS_OUTPUT.PUT_LINE('ITA OOB dashboard: Resource Analytics: Middleware already exists for: &TENANT_ID, no need to add again');  
