    values (MW_RESOURCE_DSB_ID,'Resource Analytics: Middleware',1,'Perform Resource Analytics for Oracle Fusion Middleware based on Heap, CPU Usage etc.',SYS_EXTRACT_UTC(SYSTIMESTAMP),SYS_EXTRACT_UTC(SYSTIMESTAMP),null,'Oracle',1,2,0,0,'&TENANT_ID',
    mwperf_screenshot);
    
    Insert into EMS_DASHBOARD_TILE (TILE_ID,DASHBOARD_ID,CREATION_DATE,LAST_MODIFICATION_DATE,LAST_MODIFIED_BY,OWNER,TITLE,HEIGHT,WIDTH,IS_MAXIMIZED,POSITION,TENANT_ID,
    WIDGET_UNIQUE_ID,WIDGET_NAME,WIDGET_DESCRIPTION,WIDGET_GROUP_NAME,WIDGET_ICON,WIDGET_HISTOGRAM,WIDGET_OWNER,WIDGET_CREATION_TIME,WIDGET_SOURCE,WIDGET_KOC_NAME,WIDGET_VIEWMODE,WIDGET_TEMPLATE,PROVIDER_NAME,PROVIDER_VERSION,PROVIDER_ASSET_ROOT) 
    values (MW_RESOURCE_DSB_TILE_ID,MW_RESOURCE_DSB_ID,SYS_EXTRACT_UTC(SYSTIMESTAMP),null,null,'Oracle','Home',220,2,0,0,'&TENANT_ID','0','Invisible Name','Invisible Description','Invisible Widget Group','Invisible icon','Invisible historgram','Oracle',to_char(SYS_EXTRACT_UTC(SYSTIMESTAMP),'YYYY-MM-DD"T"HH24:MI:SS.ff3"Z"'),0,'DF_V1_WIDGET_ONEPAGE','../emcsDependencies/widgets/onepage/js/onepageModel','../emcsDependencies/widgets/onepage/onepageTemplate.html','EmcitasApplications','0.1','verticalApplication.mw-resource');

    Insert into EMS_DASHBOARD_LAST_ACCESS (DASHBOARD_ID,ACCESSED_BY,ACCESS_DATE,TENANT_ID) 
    values (MW_RESOURCE_DSB_ID,'Oracle',SYS_EXTRACT_UTC(SYSTIMESTAMP),'&TENANT_ID');
    COMMIT;
    DBMS_OUTPUT.PUT_LINE('Add ITA OOB dashboard: Resource Analytics: Middleware successfully for tenant: &TENANT_ID');  
  END IF;
EXCEPTION
WHEN OTHERS THEN
  ROLLBACK;
  DBMS_OUTPUT.PUT_LINE('Failed to ITA OOB dashboard: Resource Analytics: Middleware for tenant: &TENANT_ID due to '||SQLERRM);   
  RAISE;
END;
/

REM Update ITA OOB dashboard(MW) according to latest LA side change
DECLARE
  V_COUNT NUMBER;
  DSB_ID NUMBER;
  ita_mw_perf_screenshot CLOB :=  'data:image/png;base64,'||
''||
''||
''||
''||
''||
''||
''||
''||
''||
'';
BEGIN
  SELECT COUNT(*) INTO V_COUNT FROM EMS_DASHBOARD WHERE NAME='Performance Analytics: Middleware Datasource' and TENANT_ID='&TENANT_ID' and OWNER='Oracle' and DELETED=0;
  IF (V_COUNT=0) THEN
    DBMS_OUTPUT.PUT_LINE('ITA OOB dashboard(MW) has been changed for: &TENANT_ID, no need to update again');  
  ELSE
	SELECT DASHBOARD_ID INTO DSB_ID FROM EMS_DASHBOARD WHERE NAME='Performance Analytics: Middleware Datasource' and TENANT_ID='&TENANT_ID' and OWNER='Oracle' and DELETED=0;
	DELETE FROM EMS_DASHBOARD_TILE WHERE DASHBOARD_ID=DSB_ID and TENANT_ID = '&TENANT_ID';
    DELETE FROM EMS_DASHBOARD WHERE DASHBOARD_ID=DSB_ID and TENANT_ID='&TENANT_ID';
    UPDATE EMS_DASHBOARD SET SCREEN_SHOT = ita_mw_perf_screenshot, LAST_MODIFICATION_DATE=SYS_EXTRACT_UTC(SYSTIMESTAMP) WHERE TENANT_ID='&TENANT_ID' and DASHBOARD_ID=4; 
    COMMIT;
    DBMS_OUTPUT.PUT_LINE('Update ITA OOB dashboard(MW) in Schema object: EMS_DASHBOARD successfully for tenant: &TENANT_ID');  
  END IF;
EXCEPTION
WHEN OTHERS THEN
  ROLLBACK;
  DBMS_OUTPUT.PUT_LINE('Failed to update ITA OOB dashboard(MW) in Schema object: EMS_DASHBOARD for tenant: &TENANT_ID due to '||SQLERRM);   
  RAISE;
END;
/
